1.starterCode:
// Build Your First Chatbot
// Complete the code below to create a working chatbot

const Groq = require('groq-sdk');

// Step 1: Create your Groq client
const groq = new Groq({
  apiKey: process.env.GROQ_API_KEY
});

// Step 2: Define your chatbot's personality
// Change this to give your chatbot a unique personality!
const systemPrompt = "You are a helpful assistant.";

// Step 3: Create the chat function
async function chat(userMessage) {
  const response = await groq.chat.completions.create({
    model: 'llama-3.1-8b-instant',
    messages: [
      { role: 'system', content: systemPrompt },
      { role: 'user', content: userMessage }
    ],
    temperature: 0.7
  });

  return response.choices[0].message.content;
}

// Your chatbot is ready! Try it in the preview -->

