// Build Your First Chatbot - SOLUTION

const Groq = require('groq-sdk');

const groq = new Groq({
  apiKey: process.env.GROQ_API_KEY
});

// A friendly coding tutor chatbot
const systemPrompt = `You are CodeBuddy, a friendly and encouraging coding tutor. 
You help beginners learn programming with:
- Simple explanations using real-world analogies
- Short code examples when helpful
- Encouragement and positive reinforcement
- Breaking down complex topics into small steps

Keep responses concise and beginner-friendly.`;

async function chat(userMessage) {
  const response = await groq.chat.completions.create({
    model: 'llama-3.1-8b-instant',
    messages: [
      { role: 'system', content: systemPrompt },
      { role: 'user', content: userMessage }
    ],
    temperature: 0.7
  });

  return response.choices[0].message.content;
}

// Try asking: "What is a variable?" or "How do loops work?"

2.starterCode:
// Prompt Engineering Challenge
// Master different prompting techniques!

const Groq = require('groq-sdk');

const groq = new Groq({
  apiKey: process.env.GROQ_API_KEY
});

// TECHNIQUE 1: Role Prompting
// Give the AI a specific role/persona
const systemPrompt = `You are an expert code reviewer.
When reviewing code, you:
- Point out bugs and issues
- Suggest improvements
- Explain WHY something is good or bad
- Rate code quality from 1-10`;

// TECHNIQUE 2: Few-Shot Prompting
// Provide examples to guide the AI's responses
// Try adding examples in your messages!

// TECHNIQUE 3: Chain of Thought
// Ask the AI to "think step by step"

async function chat(userMessage) {
  const response = await groq.chat.completions.create({
    model: 'llama-3.1-8b-instant',
    messages: [
      { role: 'system', content: systemPrompt },
      { role: 'user', content: userMessage }
    ],
    temperature: 0.3  // Lower = more focused
  });

  return response.choices[0].message.content;
}

// Try: "Review this code: function add(a,b) { return a + b }"

